export interface Attendance {
  id: string;
  userId: string;
  date: string;
  inTime: string;
  outTime: string;
  status: 'Present' | 'Absent' | 'Half' | 'Leave';
  notes?: string;
}

export interface Leave {
  id: string;
  userId: string;
  type: 'Annual' | 'Sick' | 'Casual';
  start: string;
  end: string;
  status: 'Pending' | 'Approved' | 'Rejected' | 'Cancelled';
  reason: string;
}

export interface PayrunItem {
  userId: string;
  gross: number;
  deductions: number;
  net: number;
}

export interface Payrun {
  id: string;
  month: string;
  generatedBy: string;
  totalPayroll: number;
  items: PayrunItem[];
}

export interface Settings {
  pfPercent: number;
  professionalTax: number;
}

export interface Notification {
  id: string;
  userId: string;
  message: string;
  timestamp: string;
  read: boolean;
}

export interface DataStore {
  users: any[];
  attendance: Attendance[];
  leaves: Leave[];
  payruns: Payrun[];
  settings: Settings;
  notifications: Notification[];
}

const STORAGE_KEY = 'workzen_demo_data';

export function getDataStore(): DataStore {
  const data = localStorage.getItem(STORAGE_KEY);
  if (!data) return initializeDataStore();
  return JSON.parse(data);
}

export function saveDataStore(data: DataStore) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
}

export function resetDataStore() {
  const data = initializeDataStore();
  localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
  return data;
}

function initializeDataStore(): DataStore {
